using System; using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI;  public class Pointer : MonoBehaviour {      public bool visible;     public float length = 100;     public Vector2 direction;      void Start()     {              }      // Update is called once per frame     void Update()     {         if (direction.y < 0) visible = false;         float dir = ((float)((360f/(2*Math.PI)) *Math.Acos(Vector2.Dot(direction.normalized, new Vector2(1, 0).normalized)))-90);         Debug.Log(direction);         var realLength = length+20;         var image = GetComponent<Image>();         image.enabled = visible;         image.color = new Color(image.color.r, image.color.g, image.color.b, direction.y / 150);         //transform.localScale = new Vector2(transform.localScale.x, length);         GetComponent<RectTransform>().SetSizeWithCurrentAnchors(RectTransform.Axis.Vertical, realLength);         transform.rotation = Quaternion.Euler(0, 0, dir);      } } 